<div id="critical-alert-banner" style="display:none; position:fixed; top:0; left:0; width:100%; z-index:10000; box-shadow: 0 4px 24px rgba(0,0,0,0.15);" class="bg-red-700 text-white text-center py-3 font-bold text-lg shadow-lg animate-bounce">
    <span id="critical-alert-banner-message">Critical alert!</span>
    <button onclick="document.getElementById('critical-alert-banner').style.display='none'" class="ml-4 bg-white text-red-700 px-3 py-1 rounded-lg font-semibold shadow hover:bg-red-100 focus:outline-none focus:ring-2 focus:ring-red-400">Dismiss</button>
</div>
